import React, { useState } from "react";
import Markdown from "react-markdown";
import { Documentation, DocPage } from './Types/types';

const App: React.FC = () => {
  // const [url, setUrl] = useState<string>("");
  // const url
  const [docPages, setDocPages] = useState<DocPage[]>([]);
  const [selectedPage, setSelectedPage] = useState<DocPage | null>(null);


    // Replace 'YOUR_JSON_URL' with the actual URL to your JSON file.
    const url = 'https://gist.githubusercontent.com/thehappybug/65a466dcdb0908767057b80f0cb7ea5d/raw/6f10747c5feb7ce91b83392f2cee23ae06b20fe6/doc.json';

  const loadContent = async () => {
    try {
      const response = await fetch(url);
      const data: Documentation = await response.json();
      setDocPages(data.pages);
    } catch (error) {
      console.error("Error fetching the documentation:", error);
    }
  };

  return (
    <div className="app">
      <div className="input-section">
        <input value={url} onChange={(e) => setUrl(e.target.value)} placeholder="Enter documentation URL" />
        <button onClick={loadContent}>Load</button>
      </div>
      <div className="documentation-section">
        <div className="side-menu">
          {docPages.map((page, index) => (
            <div
              key={index}
              className={`menu-item ${selectedPage?.title === page.title ? "active" : ""}`}
              onClick={() => setSelectedPage(page)}
            >
              {page.title}
            </div>
          ))}
        </div>
        <div className="content-area">
          <h2>{selectedPage?.title}</h2>
          <Markdown source={selectedPage?.body || ""} />
        </div>
      </div>
    </div>
  );
};

export default App;
